package dev;

public class devd {
	


	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String str = "Este es un demo de strings";
		String str2 = " y no esta vacio";
		
		System.out.println(str.length());
		System.out.println(str.concat(str2).length());
		System.out.println(str.charAt(5));
		System.out.println(str.contains("es un"));
		System.out.println(str.startsWith("Este"));
		System.out.println(str2.length());
		System.out.println(str.length());
		System.out.println(str.length());
	}

}



